import styled from 'styled-components';

export const Container = styled.div`
  width: 100%;
  height: 100vh;

  .firstArticle {
    width: 100%;
    height: 100vh;
  }

  .firstImage {
    width: 100%;
    height: 100vh;
    background: #FEEDD9;
  }

  > p {
    text-align: center;
  }

  .button {
    width: 50px;
    height: 50px;
    position: absolute;
    top: 574px;
    bottom: 0;
    right: 0;
    left: 0;
    z-index: 99;
    cursor: pointer;
  }
`;
